mapper.select:
SELECT *
	FROM(
		SELECT ROWNUM RN, MYNO, MYNAME, MYTITLE, MYCONTENT, MYDATE
			FROM(
				SELECT MYNO, MYNAME, MYTITLE, MYCONTENT, MYDATE
				FROM MYBOARD
				ORDER BY MYNO DESC
				)A
		)
WHERE RN BETWEEN #{start} AND #{end}

SELECT MYNO, MYNAME, MYTITLE, MYCONTENT, MYDATE
FROM MYBOARD
WHERE MYNO = #{myno}

mapper.update:
UPDATE MYBOARD
SET MYTITLE = #{mytitle}, MYCONTENT = #{mycontent}
WHERE MYNO = #{myno}
