mapper.select:
SELECT * FROM( SELECT ROWNUM RN, NOTICE_NO, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGDATE FROM( SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGDATE FROM BOARD_NOTICE ORDER BY NOTICE_NO DESC
AND NOTICE_TITLE like CONCAT('%', #{keyword}, '%')
AND NOTICE_CONTENT like CONCAT ('%', #{keyword}, '%')
)A ) WHERE RN BETWEEN #{start} AND #{end}

	 	SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_REGDATE
		FROM BOARD_NOTICE
 		WHERE NOTICE_NO = #{notice_no}

mapper.update:
		UPDATE BOARD_NOTICE
      	SET NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
     	WHERE NOTICE_NO = #{notice_no}
