mapper.select:
SELECT ui.USER_NO, ui.USER_ID, ui.USER_PW, ui.USER_NAME, ui.USER_EMAIL, 
	ui.USER_ROLE, ui.USER_PHOTO, ui.USER_ACTIVE, ui.USER_REGDATE --, ui.USER_DISTINCT
FROM USER_INFO ui 
WHERE USER_ID = #{user_id}
AND USER_PW = #{user_pw}
AND USER_ACTIVE = 'Y'

mapper.update:
UPDATE USER_INFO SET USER_PW = #{user_pw}, USER_NAME = #{user_name},
USER_EMAIL = #{user_email}, USER_DISTINCT = #{user_distinct}
WHERE USER_NO = #{user_no}

mapper.select:
SELECT COUNT(user_id)
FROM USER_INFO
WHERE USER_ID = #{user_id}
